model.analytics_engineering_marts.intm_k.raw_sql:
{{
  config({
		"materialized": 'ephemeral'
  })
}}
select src_country, src_channel, min(broadcast_date) as broadcast_start_date, sum(watched_mins) as total_watched_mins
from {{source('fds_kntr','fact_kntr_wwe_telecast_data')}}
where demographic_type = 'Everyone'
group by 1, 2
having datediff(d, min(broadcast_date) :: date, current_date) > 365

{{
  config({
		"materialized": 'ephemeral'
  })
}}
select a.src_country, b.broadcast_start_date,  sum(a.watched_mins) as country_watched_mins
from {{source('fds_kntr','fact_kntr_wwe_telecast_data')}} a
join (select distinct src_country, broadcast_start_date   from {{ref('intm_kntr_country_channel_vh')}}) b on a.src_country = b.src_country
where a.demographic_type = 'Everyone' and a.broadcast_date >= b.broadcast_start_date
group by 1, 2

{{
  config({
		"materialized": 'ephemeral'
  })
}}
select modified_month, region, demographic_type, demographic_group_name, sum(viewing_hours) as regional_viewing_hours
from {{ref('intm_kntr_schedule_vh_data')}}
group by 1,2,3,4

{{
  config({
		'schema': 'fds_kntr',
		"materialized": 'ephemeral'
  })
}}

select to_char(broadcast_date :: date, 'YYYYMM') as broadcast_month_year,
case 
   when upper(a.src_country)='UNITED ARAB EMIRATES' then 'APAC except India' 
        else b.region end as region,
       src_country, 
       broadcast_network_prem_type, 
	   src_demographic_group, 
	   src_demographic_age, 
sum(watched_mins/60) as viewing_hours 
from {{source('fds_kntr','fact_kntr_wwe_telecast_data')}} a
left join {{source('fds_kntr','kantar_static_country_region_tag')}}  b on upper(a.src_country) = upper(b.country)
where to_char(broadcast_date :: date, 'YYYYMM') < to_char(current_date, 'YYYYMM')
group by 1,2,3,4,5,6

{{
  config({
		"materialized": 'ephemeral'
  })
}}
select a.src_country, a.src_channel
from {{ref('intm_kntr_country_channel_vh')}} a 
join {{ref('intm_kntr_country_vh')}} b on a.src_country = b.src_country and a.broadcast_start_date = b.broadcast_start_date
where a.total_watched_mins/b.country_watched_mins <= 0.01

{{
  config({
		'schema': 'fds_kntr',
		"materialized": 'ephemeral'
  })
}}

select to_char((add_months(current_date, -1)), 'YYYYMM') as broadcast_month_year, 
region, 
src_country, 
broadcast_network_prem_type, 
src_demographic_group,
 src_demographic_age 
 from (
select  
region, src_country, broadcast_network_prem_type, src_demographic_group, src_demographic_age ,
max(broadcast_month_year) as max_mon_year 
 from {{ref('intm_kntr_monthly_country_vh')}}
group by 1,2,3,4,5 
) where max_mon_year <> to_char((add_months(current_date, -1)), 'YYYYMM')

