model.analytics_engineering_marts.interm.raw_sql:
{{
  config({
		'schema': 'fds_nl',
		"materialized": 'ephemeral'
  })
}}

 select  a.coverage_area,a.src_market_break,a.src_demographic_group,
a.broadcast_Date,a.src_broadcast_network_name,a.time_minute,absolute_network_number,
 round((a.absolute_network_number/nullif(a.most_current_us_audience_avg_proj_000,0))*100 ,5)
 as stay_percent from {{ref('intermediate_nl_switching_absolute_network_num')}} a
 where  (src_broadcast_network_name,switching_behavior_dist_cd)
in (('USA','usa'),('FOX Affiliates','fox_affiliates'),('TNT','tnt'))

{{
  config({
		'schema': 'fds_nl',
		"materialized": 'ephemeral'
  })
}}

select a.broadcast_Date,a.coverage_area,a.src_market_break,a.src_broadcast_network_name,a.src_demographic_group,
a.time_minute,a.source_name,
most_current_us_audience_avg_proj_000,
sum(absolute_network_number) as absolute_network_number
from {{ref('intermediate_nl_switching_absolute_network_num')}}  a 
group by 1,2,3,4,5,6,7,8

{{
  config({
		'schema': 'fds_nl',
		"materialized": 'ephemeral'
  })
}}

select  src_market_break,src_demographic_group,broadcast_date,mxm_source,
case when upper(src_broadcast_network_name)= 'FOX' then 'FOX Affiliates' 
when upper(src_broadcast_network_name) = 'TURNER NETWORK TELEVISION' then 'TNT'
WHEN  upper(src_broadcast_network_name) = 'USA NETWORK' THEN 'USA' END AS src_broadcast_network_name,
((split_part(program_telecast_rpt_starttime, ':', 1) :: int *60*60 +
 split_part(program_telecast_rpt_starttime, ':', 2) :: int*60 +
  (split_part(program_telecast_rpt_starttime, ':', 3) :: int  ))
 + (min_of_pgm_value - 1)*60) AS TIME_MINUTE ,
most_current_us_audience_avg_proj_000,etl_insert_rec_dttm
FROM  {{source('fds_nl','fact_nl_minxmin_ratings')}} 
where src_playback_period_cd in ('Live | TV with Digital | Linear with VOD')

{{
  config({
		'schema': 'fds_nl',
		"materialized": 'ephemeral'
  })
}}



 select a.broadcast_Date,a.src_broadcast_network_name,a.src_demographic_group,
 a.time_minute,
 dense_rank() over(partition by src_broadcast_network_name,broadcast_Date,src_demographic_group  order by 
switch_percent desc NULLS LAST)
as switch_percent_rank
from {{ref('intermediate_nl_absolute_switch_stay_detail')}}  a
where ((lower(a.source_name) in ('nxt','smackdown') and time_minute  between '20:05:00'
and '21:54:00') or (lower(a.source_name) in ('raw') and time_minute  between '20:05:00'
and '22:54:00')  or 
( lower(a.source_name)  in ('aew'))) and  comment is null

{{
  config({
		'schema': 'fds_nl',
		"materialized": 'ephemeral'
  })
}}


 select * from {{ref('intermediate_nl_switching_absolute_network_num')}}
 where   switching_behavior_dist_cd in ('set_off_off_air')

model.analytics_engineering_marts.vw_agg.raw_sql:
/*
*************************************************************************************************************************************************
   Date        : 07/21/2020
   Version     : 1.0
   TableName   : vw_aggr_nl_yearly_fin_wwe_live_program_ratings
   Schema	   : fds_nl
   Contributor : Hima Dasan
   Description : WWE Live Program Rating Yearly Finance Aggregate View consist of rating details of all WWE Live - RAW, SD, NXT Programs to be rolled up from WWE Program Ratings Daily Report Table on yearly-basis
*************************************************************************************************************************************************
*/

{{
  config({
	'schema': 'fds_nl',"materialized": 'view','tags': "Phase4B","persist_docs": {'relation' : true, 'columns' : true}
	})
}}

select broadcast_fin_year, src_broadcast_network_id, src_playback_period_cd, src_demographic_group, src_program_id,src_series_name,
--Duration Weighted Averages are taking here for avg_audience_proj_000, avg_audience_pct and avg_audience_pct_nw_cvg_area..
(sum(avg_audience_proj_000 * src_total_duration)/nullif(sum(nvl2(avg_audience_proj_000, src_total_duration, null)), 0)) as avg_audience_proj_000,
(sum(avg_audience_pct * src_total_duration)/nullif(sum(nvl2(avg_audience_pct, src_total_duration, null)), 0)) as avg_audience_pct,
(sum(avg_audience_pct_nw_cvg_area * src_total_duration)/nullif(sum(nvl2(avg_audience_pct_nw_cvg_area, src_total_duration, null)), 0)) as avg_audience_pct_nw_cvg_area, sum(viewing_minutes_units) as tot_viewing_minutes, count(*) as number_of_airings
from {{ref('rpt_nl_daily_wwe_program_ratings')}}
where broadcast_fin_year is not null and
(src_broadcast_network_id, src_program_id) in ((5, 296881), (5, 339681), (5, 436999), (81, 898521), (10433, 1000131))
group by 1,2,3,4,5,6

